    static void PlayJogo()
    {
        Console.Clear();
        Console.WriteLine("Bem-vindo ao Éden...");
        Console.WriteLine("Escolha um personagem pelo nome:");

        foreach (var p in personagens)
        {
            Console.WriteLine($"- {p.NomePersonagem}");
        }

        string nomeEscolhido = Console.ReadLine();
        Personagem jogador = personagens.Find(p => p.NomePersonagem.Equals(nomeEscolhido, StringComparison.OrdinalIgnoreCase));

        if (jogador == null)
        {
            Console.WriteLine("Personagem não encontrado. Aperte qualquer tecla para tentar novamente.");
            Console.ReadKey();
            PlayJogo();
            return;
        }

        Console.WriteLine("\nVocê escolheu:");
        jogador.ExibirInfoPersonagem();
        jogador.FalaPersonagem();

        EscolherOponente(jogador);
    }

