    static int RolarDado()
    {
        MostrarImagemDado();
        return random.Next(1, 11);
    }

    static void Batalha(Personagem jogador, Criatura oponente)
    {
        double vidaJogador = jogador.VidaPersonagem;
        double vidaOponente = oponente.VidaCriatura;

        Console.Clear();
        Console.WriteLine($"Batalha: {jogador.NomePersonagem} VS {oponente.NomeCriatura}");
        oponente.ExibirInfoCriatura();

        while (vidaJogador > 0 && vidaOponente > 0)
        {
            Console.WriteLine("\nPressione qualquer tecla para atacar...");
            Console.ReadKey();

            int dado = RolarDado();
            double dano = dado <= 5 ? jogador.AtaquePersonagem / 2 : jogador.AtaquePersonagem;
            vidaOponente -= dano;
            Console.WriteLine($"{jogador.NomePersonagem} causou {dano} de dano. Vida do oponente: {Math.Max(0, vidaOponente)}");

            if (vidaOponente <= 0)
            {
                Console.WriteLine("Você venceu a batalha!");
                jogador.ExibirInfoPersonagem();
                break;
            }

            dado = RolarDado();
            dano = dado <= 5 ? oponente.AtaqueCriatura / 2 : oponente.AtaqueCriatura;
            vidaJogador -= dano;
            Console.WriteLine($"{oponente.NomeCriatura} atacou causando {dano} de dano. Vida do jogador: {Math.Max(0, vidaJogador)}");

            if (vidaJogador <= 0)
            {
                Console.WriteLine("Você foi derrotado!");
                oponente.ExibirInfoCriatura();
                break;
            }
        }

        Console.WriteLine("Batalha encerrada. Pressione qualquer tecla para voltar ao menu.");
        Console.ReadKey();
    }

    static void BatalhaBoss(Personagem jogador, Boss boss)
    {
        double vidaJogador = jogador.VidaPersonagem;
        double vidaBoss = boss.VidaCriatura;

        Console.Clear();
        Console.WriteLine($"Batalha ÉPICA: {jogador.NomePersonagem} VS {boss.NomeCriatura}");
        boss.ExibirInfoCriatura();

        while (vidaJogador > 0 && vidaBoss > 0)
        {
            Console.WriteLine("\nPressione qualquer tecla para atacar...");
            Console.ReadKey();

            int dado = RolarDado();
            double dano = dado <= 5 ? jogador.AtaquePersonagem / 2 : jogador.AtaquePersonagem;
            vidaBoss -= dano;
            Console.WriteLine($"{jogador.NomePersonagem} causou {dano} de dano. Vida do Boss: {Math.Max(0, vidaBoss)}");

            if (vidaBoss <= 0)
            {
                Console.WriteLine("Parabéns! Você derrotou Éfren e salvou Éden!");
                jogador.ExibirInfoPersonagem();
                break;
            }

            dado = RolarDado();
            bool especial = dado > 7;
            dano = especial ? boss.AtaqueCriatura * 1.5 : (dado <= 5 ? boss.AtaqueCriatura / 2 : boss.AtaqueCriatura);
            vidaJogador -= dano;

            Console.WriteLine(especial
                ? $"{boss.NomeCriatura} usou ATAQUE ESPECIAL causando {dano} de dano!"
                : $"{boss.NomeCriatura} atacou causando {dano} de dano.");
            Console.WriteLine($"Vida do jogador: {Math.Max(0, vidaJogador)}");

            if (vidaJogador <= 0)
            {
                Console.WriteLine("Você foi derrotado! Éfren permanece como Guardião do Éden...");
                boss.ExibirInfoCriatura();
                break;
            }
        }

        Console.WriteLine("Batalha encerrada. Pressione qualquer tecla para voltar ao menu.");
        Console.ReadKey();
    }
