// ===================== CRIATURAS =====================
public class Criatura
{
    public string NomeCriatura;
    public double VidaCriatura;
    public double AtaqueCriatura;
    public double DefesaCriatura;
    public string Imagem;

    public Criatura(string nome, double vida, double ataque, double defesa, string imagem)
    {
        NomeCriatura = nome;
        VidaCriatura = vida;
        AtaqueCriatura = ataque;
        DefesaCriatura = defesa;
        Imagem = imagem;
    }

    public virtual void EmitirSomCriatura() => Console.WriteLine("Som genérico");
    public virtual void ExibirInfoCriatura()
    {
        Console.WriteLine(Imagem);
        Console.WriteLine($"Nome: {NomeCriatura}");
        Console.WriteLine($"Vida: {VidaCriatura}");
        Console.WriteLine($"Ataque: {AtaqueCriatura}");
        Console.WriteLine($"Defesa: {DefesaCriatura}");
    }
}

